"use strict";(self.webpackChunksat=self.webpackChunksat||[]).push([[648],{1866:t=>{t.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Motivation","href":"/security-analysis-tool/docs/motivation","docId":"motivation","unlisted":false},{"type":"category","label":"Installation","collapsible":true,"collapsed":true,"items":[{"type":"category","label":"Standard Install","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"AWS","href":"/security-analysis-tool/docs/installation/standard/aws","docId":"installation/standard/aws","unlisted":false},{"type":"link","label":"Azure","href":"/security-analysis-tool/docs/installation/standard/azure","docId":"installation/standard/azure","unlisted":false},{"type":"link","label":"GCP","href":"/security-analysis-tool/docs/installation/standard/gcp","docId":"installation/standard/gcp","unlisted":false}],"href":"/security-analysis-tool/docs/installation/standard/"},{"type":"category","label":"Terraform Install","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"AWS","href":"/security-analysis-tool/docs/installation/terraform/aws","docId":"installation/terraform/aws","unlisted":false},{"type":"link","label":"Azure","href":"/security-analysis-tool/docs/installation/terraform/azure","docId":"installation/terraform/azure","unlisted":false},{"type":"link","label":"GCP","href":"/security-analysis-tool/docs/installation/terraform/gcp","docId":"installation/terraform/gcp","unlisted":false}],"href":"/security-analysis-tool/docs/installation/terraform/"}],"href":"/security-analysis-tool/docs/installation/"},{"type":"link","label":"Funtionality","href":"/security-analysis-tool/docs/functionality","docId":"functionality","unlisted":false},{"type":"link","label":"FAQ","href":"/security-analysis-tool/docs/faq","docId":"faq","unlisted":false},{"type":"link","label":"Troubleshooting","href":"/security-analysis-tool/docs/troubleshooting","docId":"troubleshooting","unlisted":false}]},"docs":{"faq":{"id":"faq","title":"FAQ","description":"SAT Deployment and Support","sidebar":"tutorialSidebar"},"functionality":{"id":"functionality","title":"Funtionality","description":"Security Analysis Tool (SAT) is an observability tool that aims to improve the security hardening of Databricks deployments by making customers aware of deviations from established security best practices by helping customers monitor the security health of Databricks account workspaces easily. There is a need for a master checklist that prioritizes the checks by severity and running this as a routine scan for all the workspaces helps ensure continuous adherence to best practices.","sidebar":"tutorialSidebar"},"installation/index":{"id":"installation/index","title":"Installation","description":"SAT v0.2.0* or higher brings full support for Unity Catalog. Now you can pick your catalog instead of hive_metastore. Plus, you get to choose your own schema name.","sidebar":"tutorialSidebar"},"installation/standard/aws":{"id":"installation/standard/aws","title":"AWS","description":"This guide will help you setup the Security Analysis Tool (SAT) on AWS Databricks.","sidebar":"tutorialSidebar"},"installation/standard/azure":{"id":"installation/standard/azure","title":"Azure","description":"This guide will help you setup the Security Analysis Tool (SAT) on Azure Databricks.","sidebar":"tutorialSidebar"},"installation/standard/gcp":{"id":"installation/standard/gcp","title":"GCP","description":"This guide will help you setup the Security Analysis Tool (SAT) on GCP Databricks.","sidebar":"tutorialSidebar"},"installation/standard/index":{"id":"installation/standard/index","title":"Standard Install","description":"SAT is a productivity tool to help verify security configurations of Databricks deployments, it\'s not meant to be used as certification or attestation of your deployments. SAT project is regularly updated to improve the correctness of checks, add new checks, and fix bugs. You will need a single SAT install per Databricks account in AWS and GCP and a single install per azure subscription in Azure. Add the Service principal as mentioned in the detailed steps to analyze the rest of the workspaces from the workspace where SAT is installed. You can choose not to add SP to a given workspace if you wish to ignore a given workspace. Please send your feedback and comments to sat@databricks.com.","sidebar":"tutorialSidebar"},"installation/terraform/aws":{"id":"installation/terraform/aws","title":"AWS","description":"Setting up Terraform","sidebar":"tutorialSidebar"},"installation/terraform/azure":{"id":"installation/terraform/azure","title":"Azure","description":"Setting up Terraform for Azure","sidebar":"tutorialSidebar"},"installation/terraform/gcp":{"id":"installation/terraform/gcp","title":"GCP","description":"Setting up Terraform","sidebar":"tutorialSidebar"},"installation/terraform/index":{"id":"installation/terraform/index","title":"Terraform Install","description":"SAT can be setup on any of the cloud providers where Databricks is hosted and can be installed using Terrafom. Follow the setup guide for the cloud provider you are using:","sidebar":"tutorialSidebar"},"motivation":{"id":"motivation","title":"Motivation","description":"Security Analysis Tool (SAT) analyzes customer\'s Databricks account and workspace security configurations and provides recommendations that help them follow Databrick\'s security best practices.","sidebar":"tutorialSidebar"},"troubleshooting":{"id":"troubleshooting","title":"Troubleshooting","description":"We created diagnosis notebooks for respective clouds to help troubleshoot your SAT setup. Please go to Workspace -> Applications -> SAT -> files -> notebooks -> diagnosis/ folder and run the respective cloud satdiagnosis notebook.","sidebar":"tutorialSidebar"}}}}')}}]);